import React, { useState, useEffect } from "react";
import axios from "axios";

const NewsContent = () => {
  const [newsArray, setNewsArray] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "https://newsapi.org/v2/everything?q=Apple&from=2024-01-25&sortBy=popularity&apiKey=58f4cbe7850b46bc8b1b599ce7df94b6"
        );

        if (response.data.status === "ok") {
          setNewsArray(response.data.articles);
        }
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchNews();
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  return (
    <div className="app-content">
      {newsArray.map((props, index) => {
        const {
          author,
          title,
          publishedAt,
          description,
          urlToImage,
          url,
          source: { name },
        } = props;

        return (
          <div className="newsCard" key={index}>
            <img
              className="newsImage"
              src={
                urlToImage
                  ? urlToImage
                  : "https://www.unityhighschool.org/wp-content/uploads/2014/08/default-placeholder.png"
              }
              alt="News"
            />
            <div className="newsText">
              {" "}
              <span className="title">
                {title}
                <br />
                <span className="author">
                  <a href={url} target="_blank" rel="noreferrer">
                    <b>short</b>{" "}
                  </a>
                  <span className="muted">
                    by {author ? author : "unknown"} on{" "}
                    <span className="publishedAt">{publishedAt}</span>
                  </span>
                </span>
                <div className="lowerNewsText">
                  <div className="description">
                    <p>{description}</p>
                  </div>
                </div>
              </span>
              <div>
                <span className="readmore">
                  {" "}
                  <a href={url} target="_blank" rel="noreferrer">
                    read more at{" "}
                    <span style={{ color: "black" }}>
                      {" "}
                      <b>{name}</b>{" "}
                    </span>{" "}
                  </a>
                </span>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default NewsContent;
